import React, { useState, useEffect} from "react";
import Modal from "react-modal";
import "./App.css";
// import Select from 'react-select'
import { v4 as uuidv4 } from "uuid";
// for modal reusability
import ModalRoot from './modules/modals/components/ModalRoot';
import ModalService from './modules/modals/services/ModalService';
import TestModal from './components/TestModal';
import NewTicketModal from "./components/NewTicketModal"
Modal.setAppElement("#root");

// const options = [
//   { value: 'bug', label: 'Bug' },
//   { value: 'feature', label: 'Feature' },
//   { value: 'request', label: 'Request' }
// ];

const App = () => {
  //initialise from local memory
  const [projects, setProjects] = useState(projectList);
  const [modalIsOpen, setIsOpen] = useState(false);
  const [title, setTitle] = useState("");
  const [category, setCategory]= useState({ value: 'bug', label: 'Bug' });
  const [currentColumnId, setCurrentColumnId] = useState(null);
  const [columns, setColumns] = useState([
    { name: "Planning", stage: uuidv4() },
    { name: "Design", stage: uuidv4() },
    // { name: "In Progress", stage: uuidv4() },
    // { name: "Testing", stage: uuidv4() },
    // { name: "Launch", stage: uuidv4() },
  ]);
  const style = {
    padding: "30px",
    paddingTop: "5px",
  };
  // open particula  modal
  const addModal = () => {
    ModalService.open(TestModal, { columns, setColumns});
  };

  const addTicket = () => {
    ModalService.open(NewTicketModal, { category, setCategory, title, setTitle, createTicket, currentColumnId });
  };
  const openColumnModal=()=>{

  }

  function closeModal() {
    setIsOpen(false);
  }

  const createTicket=(stage)=>{
    console.log("stage", stage);
    console.log("projects", projects);
    setProjects(projects=> [...projects,
      {
        id: uuidv4(),
        category: category.label,
        description: title, 
        project_stage: stage,
      }
    ]
    );
    setIsOpen(false);
  };

  return (
    <div style={style}>
      <h1>Project Kanban Board</h1>
      <ModalRoot />
      <button onClick={ addModal } className="btn btn-primary m-4">Open modal</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Example Modal"
      >
        {/* <Select options={options} value= {category} onChange={(value)=>setCategory(value) }/> */}
        <input value={title} onChange= {e=> setTitle(e.target.value)}></input>
        <button onClick={()=>createTicket(currentColumnId)}>Create Ticket</button>

      </Modal>
        <KanbanBoard setCurrentColumnId= {setCurrentColumnId} setIsOpen={setIsOpen} projects= {projects} setProjects= {setProjects}
        columns= {columns} setColumns={setColumns}
        addTicket={addTicket}
        />
    </div>
  );
};

/*
 * The Kanban Board React component
 */
const KanbanBoard = ({setIsOpen, projects, setProjects, setCurrentColumnId, columns, setColumns, addTicket}) => {
  const [isLoading, setIsLoading] = useState(false);
  const [draggedOverColumn, setDraggedOverColumn] = useState(0);
  //init from local storage, if not present, then initialise from here


  //this is called when a Kanban card is dragged over a column (called by column)
  const handleOnDragEnter = (e, stageValue) => {
    setDraggedOverColumn(stageValue);
  };

  //this is called when a Kanban card dropped over a column (called by card)
  const handleOnDragEnd = (e, project) => {
    const updatedProjects = projects.slice(0);
    updatedProjects.find((projectObject) => {
      return projectObject.id === project.id;
    }).project_stage = draggedOverColumn;
    console.log("dragged", updatedProjects);
    setProjects(updatedProjects);
  };

  // const addColumn = (colName) => {
  //   setColumns((columns) => [...columns, { name: colName, stage: uuidv4() }]);
  // };

  if (isLoading) {
    return <h3>Loading...</h3>;
  }
console.log("cols", columns);
  return (
    <div>
      {columns.map((column) => {
        return (
          <KanbanColumn
            setIsOpen={setIsOpen}
            name={column.name}
            setProjects={setProjects}
            stage={column.stage}
            setColumns= {setColumns}
            projects={projects.filter((project) => {
              return project.project_stage === column.stage;
            })}
            onDragEnter={handleOnDragEnter}
            onDragEnd={handleOnDragEnd}
            key={column.stage}
            setCurrentColumnId= {setCurrentColumnId}
            addTicket={addTicket}
          />
        );
      })}
      {/* <button onClick={addColumn}>Add Column</button> */}
    </div>
  );
};

/*
 * The Kanban Board Column React component
 */
const KanbanColumn = ({
  stage,
  name,
  projects,
  setProjects,
  onDragEnter,
  onDragEnd,
  setIsOpen,
  setColumns,
  setCurrentColumnId,
  addTicket
}) => {
  const [mouseIsHovering, setMouseIsHovering] = useState(false);
  const createCard = () => {
    setIsOpen(true);
    setCurrentColumnId(stage);
  };
  useEffect(() => {
    setMouseIsHovering(false);
  }, []);

  const deleteColumn= ()=>{
    setColumns(cols=>cols.filter(item=> item.stage!==stage))
  }

  const generateKanbanCards = () => {
    return projects.slice(0).map((project) => {
      return (
        <KanbanCard
          project={project}
          key={project.id + project.project_stage}
          onDragEnd={onDragEnd}
          setProjects={setProjects}
          id={project.id}
        />
      );
    });
  };
  const columnStyle = {
    display: "inline-block",
    verticalAlign: "top",
    marginRight: "5px",
    marginBottom: "5px",
    paddingLeft: "5px",
    paddingTop: "0px",
    width: "230px",
    textAlign: "center",
    backgroundColor: mouseIsHovering ? "#d3d3d3" : "#f0eeee",
  };
  return (
    <div
      style={columnStyle}
      onDragEnter={(e) => {
        setMouseIsHovering(true);
        onDragEnter(e, stage);
      }}
      onDragExit={(e) => {
        setMouseIsHovering(false);
      }}
    >
      {/* <button onClick={createCard}>add card</button> */}
      <button onClick={addTicket}>add card</button>
      <button onClick= {deleteColumn}>delete column</button>
      <h4>
        {stage}. {name} ({projects.length})
      </h4>
      {generateKanbanCards()}
      <br />
    </div>
  );
};

/*
 * The Kanban Board Card component
 */
const KanbanCard = ({ onDragEnd, project, setProjects, id }) => {
  const [collapsed, setCollapsed] = useState(true);
  const deleteCard = () =>
    setProjects((projects) => projects.filter((item) => item.id !== id));
  const cardStyle = {
    backgroundColor: "#f9f7f7",
    paddingLeft: "0px",
    paddingTop: "5px",
    paddingBottom: "5px",
    marginLeft: "0px",
    marginRight: "5px",
    marginBottom: "5px",
  };

  return (
    <div
      style={cardStyle}
      draggable={true}
      onDragEnd={(e) => {
        onDragEnd(e, project);
      }}
    >
      {" "}
      <div>Category: {project.category}</div> <button onClick={deleteCard}>delete</button>
      <div>
        <h4>{project.name}</h4>
      </div>
      {collapsed ? null : (
        <div>
          <strong>Description: </strong>
          {project.description}
          <br />
        </div>
      )}
      <div
        style={{ width: "100%" }}
        onClick={(e) => {
          setCollapsed((collapsed) => !collapsed);
        }}
      >
        {collapsed ? String.fromCharCode("9660") : String.fromCharCode("9650")}
      </div>
    </div>
  );
};

/*
 * Projects to be displayed on Kanban Board
 */
let projectList = [
  // {
  //   id: "sada",
  //   category: "Bug", 
  //   name: "Project 1",
  //   description:
  //     "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Etiam posuere dui vel urna egestas rutrum. ",
  //   project_stage: 1,
  // },
  // {
  //   category: "Bug", 
  //   id: "tada",
  //   name: "Project 2",
  //   description:
  //     "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Etiam posuere dui vel urna egestas rutrum. ",
  //   project_stage: 3,
  // },
  // {
  //   id: "pada",
  //   category: "Bug", 
  //   name: "Project 3",
  //   description:
  //     "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Etiam posuere dui vel urna egestas rutrum. ",
  //   project_stage: 1,
  // },
  // {
  //   id: "dada",
  //   category: "Bug", 

  //   name: "Project 4",
  //   description:
  //     "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Etiam posuere dui vel urna egestas rutrum. ",
  //   project_stage: 2,
  // },
];

export default App;
