import React, {useState, useEffect} from 'react';

const  App=()=>{

		const style = {
			'padding': '30px',
			'paddingTop': '5px',
		};

		return(
      <div style={ style }>
        <h1>Project Kanban Board</h1>
				<KanbanBoard />
      </div>
		);
    }

/*
 * The Kanban Board React component
 */
const KanbanBoard =()=>  {
  const [isLoading, setIsLoading]=  useState(false);
  const [projects, setProjects] = useState(projectList);
  const [draggedOverColumn, setDraggedOverColumn]=  useState(0);
  const [columns, setColumns]= useState(
    [
      		{name: 'Planning', stage: 1},
      		{name: 'Design', stage: 2},
      		{name: 'In Progress', stage: 3},
      		{name: 'Testing', stage: 4},
      		{name: 'Launch', stage: 5},
      	]
  );

	//this is called when a Kanban card is dragged over a column (called by column)
	const handleOnDragEnter= (e, stageValue)=> {
     setDraggedOverColumn(stageValue);

	}

	//this is called when a Kanban card dropped over a column (called by card)
	const handleOnDragEnd= (e, project) =>{
		const updatedProjects = projects.slice(0);
		updatedProjects.find((projectObject) => {return projectObject.name === project.name}).project_stage = draggedOverColumn;
		setProjects(updatedProjects);
	}

  const addColumn=(colName)=>{
    setColumns(columns=> [...columns, {name: 'New', stage: 6}]);
  }


		if (isLoading) {
			return (<h3>Loading...</h3>);
		}

		return  (
      <div>
				{columns.map((column) => {
					return (
						<KanbanColumn
							name={ column.name }
              setProjects= {setProjects}
							stage={ column.stage }
							projects={ projects.filter((project) => {return parseInt(project.project_stage, 10) === column.stage;}) }
							onDragEnter={ handleOnDragEnter }
							onDragEnd={ handleOnDragEnd }
							key={ column.stage }
						/>
					);
				})}
        <button onClick= {addColumn}>Add Column</button>
      </div>
		);

}

/*
 * The Kanban Board Column React component
 */
const KanbanColumn =({stage, name, projects, setProjects, onDragEnter, onDragEnd})=> {
 const [mouseIsHovering, setMouseIsHovering] = useState(false);

  useEffect( () => {
    setMouseIsHovering(false);
}, [])

	const generateKanbanCards=()=> {
		return projects.slice(0).map((project) => {
			return (
				<KanbanCard
					project={project}
					key={project.name}
					onDragEnd={onDragEnd}
          setProjects= {setProjects}
          id= {project.id}
				/>
			);
		});
	}
  const columnStyle = {
    'display': 'inline-block',
    'verticalAlign': 'top',
    'marginRight': '5px',
    'marginBottom': '5px',
    'paddingLeft': '5px',
    'paddingTop': '0px',
    'width': '230px',
    'textAlign': 'center',
    'backgroundColor': (mouseIsHovering) ? '#d3d3d3' : '#f0eeee',
  };

		return  (
      <div
				style={columnStyle}
				onDragEnter={(e) => {setMouseIsHovering(true); onDragEnter(e, stage)}}
				onDragExit={(e) => {setMouseIsHovering(false)}}
			>
				<h4>{stage}. {name} ({projects.length})</h4>
				{generateKanbanCards()}
				<br/>
      </div>);
}

