/*
 * The Kanban Board Column React component
 */
const KanbanColumn = ({
  stage,
  name,
  projects,
  setProjects,
  onDragEnter,
  onDragEnd,
  setColumns,
  addTicket,
}) => {
  const [mouseIsHovering, setMouseIsHovering] = useState(false);
  // useEffect(() => {
  //   setMouseIsHovering(false);
  // }, []);

  const deleteColumn = () => {
    setColumns((cols) => cols.filter((item) => item.stage !== stage));
    setProjects((proj) => proj.filter((item) => item.project_stage !== stage));
  };
  const handleNewCardAddition = () => {
    //setCurrentColumnId(stage);
    addTicket(stage);
  };
  const generateKanbanCards = () => {
    return projects.slice(0).map((project) => {
      return (
        <KanbanCard
          project={project}
          key={project.id + project.project_stage}
          onDragEnd={onDragEnd}
          setProjects={setProjects}
          id={project.id}
        />
      );
    });
  };

  //TODO: mover hover out controls for changing boundry style
  const columnStyle = {
    display: "inline-block",
    verticalAlign: "top",
    marginRight: "5px",
    marginBottom: "5px",
    paddingLeft: "5px",
    paddingTop: "0px",
    width: "230px",
    textAlign: "center",
    backgroundColor: mouseIsHovering ? "#efe2e2" : "#faf7f7",
    border: mouseIsHovering ? "dashed 2px red" : undefined,
    borderRadius: "10%",
    cursor: "copy",
  };
  return (
    <div
      style={columnStyle}
      className="kanban-column"
      onDragEnter={(e) => {
        console.log("drag rntry");
        console.log("dentry", e);
        e.target.style.transform= "rotate(20)";
        e.preventDefault();
        setMouseIsHovering(true);
        onDragEnter(e, stage);
      }}
      onDragLeave={(e) => {
        e.preventDefault();
        console.log("drag exit");
        setMouseIsHovering(false);
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          padding: "10px",
        }}
      >
        <FontAwesomeIcon onClick={handleNewCardAddition} icon={faPlus} className="addIcon"/>
        <FontAwesomeIcon onClick={deleteColumn} icon={faTrash} className="deleteIcon"/>
      </div>

      <div style={{textAlign: "left", marginLeft: "4%"}}><strong >{name} ({projects.length})</strong></div>


      {generateKanbanCards()}
      <br />
    </div>
  );
};

