import React, { useState, useEffect} from "react";
import Modal from "react-modal";
import "./App.css";
// import Select from 'react-select'
import { v4 as uuidv4 } from "uuid";
// for modal reusability
import ModalRoot from './modules/modals/components/ModalRoot';
import ModalService from './modules/modals/services/ModalService';
import TestModal from './components/TestModal';
import NewTicketModal from "./components/NewTicketModal"
import options from "./data/categories"
Modal.setAppElement("#root");

const App = () => {
  //initialise from local memory
  const [projects, setProjects] = useState(projectList);
  const [currentColumnId, setCurrentColumnId] = useState(null);
  const [columns, setColumns] = useState([
    { name: "Planning", stage: uuidv4() },
    { name: "Design", stage: uuidv4() },
    { name: "In Progress", stage: uuidv4() },
    { name: "Testing", stage: uuidv4() },
    { name: "Launch", stage: uuidv4() },
  ]);
  const style = {
    padding: "30px",
    paddingTop: "5px",
  };
  // open particular  modal
  const addModal = () => {
    ModalService.open(TestModal, { columns, setColumns});
  };

  const addTicket = (stage) => {
    ModalService.open(NewTicketModal, { createTicket, currentColumnId, stage });
  };

  const createTicket=(stage, title, category)=>{
    setProjects(projects=> [...projects,
      {
        id: uuidv4(),
        category: category.label,
        description: title, 
        project_stage: stage,
      }
    ]
    );
  };

  return (
    <div style={style}>
      <h1>Project Kanban Board</h1>
      <ModalRoot />
      <button onClick={ addModal } className="btn btn-primary m-4">Add New Column</button>
        <KanbanBoard setCurrentColumnId= {setCurrentColumnId} projects= {projects} setProjects= {setProjects}
        columns= {columns} setColumns={setColumns}
        addTicket={addTicket}
        />
    </div>
  );
};

/*
 * The Kanban Board React component
 */
const KanbanBoard = ({ projects, setProjects, setCurrentColumnId, columns, setColumns, addTicket}) => {
  const [isLoading, setIsLoading] = useState(false);
  const [draggedOverColumn, setDraggedOverColumn] = useState(0);
  //init from local storage, if not present, then initialise from here


  //this is called when a Kanban card is dragged over a column (called by column)
  const handleOnDragEnter = (e, stageValue) => {
    setDraggedOverColumn(stageValue);
  };

  //this is called when a Kanban card dropped over a column (called by card)
  const handleOnDragEnd = (e, project) => {
    const updatedProjects = projects.slice(0);
    updatedProjects.find((projectObject) => {
      return projectObject.id === project.id;
    }).project_stage = draggedOverColumn;
    console.log("dragged", updatedProjects);
    setProjects(updatedProjects);
  };

  if (isLoading) {
    return <h3>Loading...</h3>;
  }
console.log("cols", columns);
  return (
    <div>
      {columns.map((column) => {
        return (
          <KanbanColumn
            name={column.name}
            setProjects={setProjects}
            stage={column.stage}
            setColumns= {setColumns}
            projects={projects.filter((project) => {
              return project.project_stage === column.stage;
            })}
            onDragEnter={handleOnDragEnter}
            onDragEnd={handleOnDragEnd}
            key={column.stage}
            setCurrentColumnId= {setCurrentColumnId}
            addTicket={addTicket}
          />
        );
      })}
    </div>
  );
};

/*
 * The Kanban Board Column React component
 */
const KanbanColumn = ({
  stage,
  name,
  projects,
  setProjects,
  onDragEnter,
  onDragEnd,
  setIsOpen,
  setColumns,
  setCurrentColumnId,
  addTicket
}) => {
  const [mouseIsHovering, setMouseIsHovering] = useState(false);
  useEffect(() => {
    setMouseIsHovering(false);
  }, []);

  const deleteColumn= ()=>{
    setColumns(cols=>cols.filter(item=> item.stage!==stage));
    setProjects(proj=> proj.filter(item=> item.project_stage!==stage));
  }
  const handleNewCardAddition=()=>{
    //setCurrentColumnId(stage);
    addTicket(stage);
  }
  const generateKanbanCards = () => {
    return projects.slice(0).map((project) => {
      return (
        <KanbanCard
          project={project}
          key={project.id + project.project_stage}
          onDragEnd={onDragEnd}
          setProjects={setProjects}
          id={project.id}
        />
      );
    });
  };
  const columnStyle = {
    display: "inline-block",
    verticalAlign: "top",
    marginRight: "5px",
    marginBottom: "5px",
    paddingLeft: "5px",
    paddingTop: "0px",
    width: "230px",
    textAlign: "center",
    backgroundColor: mouseIsHovering ? "#d3d3d3" : "#f0eeee",
  };
  return (
    <div
      style={columnStyle}
      onDragEnter={(e) => {
        setMouseIsHovering(true);
        onDragEnter(e, stage);
      }}
      onDragExit={(e) => {
        setMouseIsHovering(false);
      }}
    >
      {/* <button onClick={createCard}>add card</button> */}
      <button onClick={handleNewCardAddition}>add card</button>
      <button onClick= {deleteColumn}>delete column</button>
      <h4>
        {stage}. {name} ({projects.length})
      </h4>
      {generateKanbanCards()}
      <br />
    </div>
  );
};

/*
 * The Kanban Board Card component
 */
const KanbanCard = ({ onDragEnd, project, setProjects, id }) => {
  const colorMapping= {
   "Bug": "red",
   "Feature": "blue",
   "Request": "green"
  };

  const [collapsed, setCollapsed] = useState(true);
  const deleteCard = () =>
    setProjects((projects) => projects.filter((item) => item.id !== id));
  const cardStyle = {
    backgroundColor: "#f9f7f7",
    paddingLeft: "0px",
    paddingTop: "5px",
    paddingBottom: "5px",
    marginLeft: "0px",
    marginRight: "5px",
    marginBottom: "5px",
  };

  return (
    <div
      style={cardStyle}
      draggable={true}
      onDragEnd={(e) => {
        onDragEnd(e, project);
      }}
    >
      {" "}
      <div style= {{color: colorMapping[project.category]}}>{project.category}</div> <button onClick={deleteCard}>delete</button>
        <div>

          <strong>{project.description}</strong>
          <br />
        </div>
    </div>
  );
};

/*
 * Projects to be displayed on Kanban Board
 */
let projectList = [
  // {
