import React, { useState } from "react";
import "./App.css";
import { v4 as uuidv4 } from "uuid";
// for modal reusability
import ModalRoot from "./modules/modals/components/ModalRoot";
import ModalService from "./modules/modals/services/ModalService";
import TestModal from "./components/NewColumnModal";
import NewTicketModal from "./components/NewTicketModal";
// Font Awesome Imports
import { faTrash, faPlus } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import hook for local storage use
import useLocallyStoredState from "./hooks/useLocallyStoredState";
const App = () => {
  const [projects, setProjects] = useLocallyStoredState(
    projectList,
    "projects"
  );

  const [columns, setColumns] = useLocallyStoredState(
    [
      { name: "Planning", stage: uuidv4() },
      { name: "Design", stage: uuidv4() },
      { name: "In Progress", stage: uuidv4() },
      { name: "Testing", stage: uuidv4() },
      { name: "Launch", stage: uuidv4() },
    ],
    "columns"
  );

  const style = {
    padding: "30px",
    paddingTop: "5px",
  };
  const addModal = () => {
    ModalService.open(TestModal, { columns, setColumns });
  };

  const addTicket = (stage) => {
    ModalService.open(NewTicketModal, { createTicket, stage });
  };

  const createTicket = (stage, title, category) => {
    setProjects((projects) => [
      ...projects,
      {
        id: uuidv4(),
        category: category.label,
        description: title,
        project_stage: stage,
      },
    ]);
  };

  return (
    <div style={style}>
      <h1 style={{textAlign: "center", marginBottom:"15px"}}>Welcome To Kanban Board</h1>
      <ModalRoot />
      <div style={{display: "inline-flex"}}>
      <div style={{maxWidth:"85vw"}}>
        <KanbanBoard
          // setCurrentColumnId={setCurrentColumnId}
          projects={projects}
          setProjects={setProjects}
          columns={columns}
          setColumns={setColumns}
          addTicket={addTicket}
        />
        </div>
        <span>
        <button onClick={addModal} className="btn btn-primary margin-style">
          New Column
        </button>
        </span>


        </div>
      </div>
  );
};

/*
 * The Kanban Board React component
 */
const KanbanBoard = ({
  projects,
  setProjects,
  // setCurrentColumnId,
  columns,
  setColumns,
  addTicket,
}) => {
  const [isLoading, setIsLoading] = useState(false);
  const [draggedOverColumn, setDraggedOverColumn] = useState(0);
  //this is called when a Kanban card is dragged over a column (called by column)
  const handleOnDragEnter = (e, stageValue) => {
    setDraggedOverColumn(stageValue);
  };

  //this is called when a Kanban card dropped over a column (called by card)
  const handleOnDragEnd = (e, project) => {
    e.preventDefault();
    const updatedProjects = projects.slice(0);
    updatedProjects.find((projectObject) => {
      return projectObject.id === project.id;
    }).project_stage = draggedOverColumn;
    console.log("dragged", updatedProjects);
    setProjects(updatedProjects);
  };

  if (isLoading) {
    return <h3>Loading...</h3>;
  }
  console.log("cols", columns);
  return (
    <div style={{ display: "flex", flexWrap: "nowrap" }}>
      {columns.map((column) => {
        return (
          <KanbanColumn
            name={column.name}
            setProjects={setProjects}
            stage={column.stage}
            setColumns={setColumns}
            projects={projects.filter((project) => {
              return project.project_stage === column.stage;
            })}
            onDragEnter={handleOnDragEnter}
            onDragEnd={handleOnDragEnd}
            key={column.stage}
            // setCurrentColumnId={setCurrentColumnId}
            addTicket={addTicket}
          />
        );
      })}
    </div>
  );
};

/*
 * The Kanban Board Column React component
 */
const KanbanColumn = ({
  stage,
  name,
  projects,
  setProjects,
  onDragEnter,
  onDragEnd,
  setColumns,
  addTicket,
}) => {
  const [mouseIsHovering, setMouseIsHovering] = useState(false);
  // useEffect(() => {
  //   setMouseIsHovering(false);
  // }, []);

  const deleteColumn = () => {
    setColumns((cols) => cols.filter((item) => item.stage !== stage));
    setProjects((proj) => proj.filter((item) => item.project_stage !== stage));
  };
  const handleNewCardAddition = () => {
    //setCurrentColumnId(stage);
    addTicket(stage);
  };
  const generateKanbanCards = () => {
    return projects.slice(0).map((project) => {
      return (
        <KanbanCard
          project={project}
          key={project.id + project.project_stage}
          onDragEnd={onDragEnd}
          setProjects={setProjects}
          id={project.id}
        />
      );
    });
  };

  //TODO: mover hover out controls for changing boundry style
  const columnStyle = {
    display: "inline-block",
    verticalAlign: "top",
    marginRight: "5px",
    marginBottom: "5px",
    paddingLeft: "5px",
    paddingTop: "0px",
    width: "230px",
    textAlign: "center",
    backgroundColor: mouseIsHovering ? "#efe2e2" : "#faf7f7",
    border: mouseIsHovering ? "dashed 2px red" : undefined,
    borderRadius: "10%",
    cursor: "copy",
  };
  return (
    <div
      style={columnStyle}
      className="kanban-column"
      onDragEnter={(e) => {
        console.log("drag rntry");
        console.log("dentry", e);
        e.target.style.transform= "rotate(20)";
        e.preventDefault();
        setMouseIsHovering(true);
        onDragEnter(e, stage);
      }}
      onDragLeave={(e) => {
        e.preventDefault();
        console.log("drag exit");
        setMouseIsHovering(false);
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          padding: "10px",
        }}
      >
        <FontAwesomeIcon onClick={handleNewCardAddition} icon={faPlus} className="addIcon"/>
        <FontAwesomeIcon onClick={deleteColumn} icon={faTrash} className="deleteIcon"/>
      </div>

      <div style={{textAlign: "left", marginLeft: "4%"}}><strong >{name} ({projects.length})</strong></div>


      {generateKanbanCards()}
      <br />
    </div>
  );
};

/*
 * The Kanban Board Card component
 */
const KanbanCard = ({ onDragEnd, project, setProjects, id }) => {
  const colorMapping = {
    Bug: "red",
    Feature: "blue",
    Request: "green",
  };

  const [collapsed, setCollapsed] = useState(true);
  const deleteCard = () =>
    setProjects((projects) => projects.filter((item) => item.id !== id));
  const cardStyle = {
    backgroundColor: "#ffffff",
    paddingLeft: "5px",
    paddingRight: "5px",
    paddingTop: "5px",
    paddingBottom: "5px",
    marginLeft: "0px",
    marginRight: "5px",
    marginBottom: "5px",
    borderRadius: "4px",
  };
  const circleStyle = {
    display: "inline-flex",
    width: 10,
    height: 10,
    borderRadius: "50%",
  };

  return (
    <div
      style={cardStyle}
      draggable={true}
      onDragEnd={(e) => {
        onDragEnd(e, project);
      }}
    >
      {" "}
      <div
        style={{
          color: colorMapping[project.category],
          display: "flex",
          justifyContent: "space-between",
        }}
      >
        <div style={{ display: "inline-block" }}>
          <div
            style={{
              ...circleStyle,
              backgroundColor: colorMapping[project.category],
            }}
          ></div>{" "}
          {project.category}
        </div>
        <FontAwesomeIcon onClick={deleteCard} icon={faTrash} className="deleteIcon delete-icon-margin" size= {"sm"}/>
        {/* <button onClick={deleteCard}>delete</button> */}
      </div>
      <div style={{ display: "flex", justifyContent: "flex-start", marginLeft: "7%" }}>
        <strong>{project.description}</strong>
        <br />
      </div>
    </div>
  );
};

/*
 * Projects to be displayed on Kanban Board
 */
let projectList = [];

export default App;
