import React, { useState, useEffect, useContext, createContext } from "react";
import Modal from "react-modal";
Modal.setAppElement("#root");

const App = () => {
  const style = {
    padding: "30px",
    paddingTop: "5px",
  };


  const [modalIsOpen, setIsOpen] = useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div style={style}>
      <h1>Project Kanban Board</h1>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Example Modal"
      >
        <button onClick={closeModal}>close</button>
        <div>I am a modal</div>
      </Modal>
        <KanbanBoard setIsOpen={setIsOpen}/>
    </div>
  );
};

/*
 * The Kanban Board React component
 */
const KanbanBoard = ({setIsOpen}) => {
  const [isLoading, setIsLoading] = useState(false);
  const [projects, setProjects] = useState(projectList);
  const [draggedOverColumn, setDraggedOverColumn] = useState(0);
  const [columns, setColumns] = useState([
    { name: "Planning", stage: 1 },
    { name: "Design", stage: 2 },
    { name: "In Progress", stage: 3 },
    { name: "Testing", stage: 4 },
    { name: "Launch", stage: 5 },
  ]);

  //this is called when a Kanban card is dragged over a column (called by column)
  const handleOnDragEnter = (e, stageValue) => {
    setDraggedOverColumn(stageValue);
  };

  //this is called when a Kanban card dropped over a column (called by card)
  const handleOnDragEnd = (e, project) => {
    const updatedProjects = projects.slice(0);
    updatedProjects.find((projectObject) => {
      return projectObject.name === project.name;
    }).project_stage = draggedOverColumn;
    setProjects(updatedProjects);
  };

  const addColumn = (colName) => {
    setColumns((columns) => [...columns, { name: "New", stage: 6 }]);
  };

  if (isLoading) {
    return <h3>Loading...</h3>;
  }

  return (
    <div>
      {columns.map((column) => {
        return (
          <KanbanColumn
            setIsOpen={setIsOpen}
            name={column.name}
            setProjects={setProjects}
            stage={column.stage}
            projects={projects.filter((project) => {
              return parseInt(project.project_stage, 10) === column.stage;
            })}
            onDragEnter={handleOnDragEnter}
            onDragEnd={handleOnDragEnd}
            key={column.stage}
          />
        );
      })}
      <button onClick={addColumn}>Add Column</button>
    </div>
  );
};

/*
 * The Kanban Board Column React component
 */
const KanbanColumn = ({
  stage,
  name,
  projects,
  setProjects,
  onDragEnter,
  onDragEnd,
  setIsOpen
}) => {
  const [mouseIsHovering, setMouseIsHovering] = useState(false);
  const createCard = () => {};
  useEffect(() => {
    setMouseIsHovering(false);
  }, []);

  const generateKanbanCards = () => {
    return projects.slice(0).map((project) => {
      return (
        <KanbanCard
          project={project}
          key={project.name}
          onDragEnd={onDragEnd}
          setProjects={setProjects}
          id={project.id}
        />
      );
    });
  };
  const columnStyle = {
    display: "inline-block",
    verticalAlign: "top",
    marginRight: "5px",
    marginBottom: "5px",
    paddingLeft: "5px",
    paddingTop: "0px",
    width: "230px",
    textAlign: "center",
    backgroundColor: mouseIsHovering ? "#d3d3d3" : "#f0eeee",
  };
  return (
    <div
      style={columnStyle}
      onDragEnter={(e) => {
        setMouseIsHovering(true);
        onDragEnter(e, stage);
      }}
      onDragExit={(e) => {
        setMouseIsHovering(false);
      }}
    >
      <button onClick={()=>setIsOpen(true)}>add card</button>
      <h4>
        {stage}. {name} ({projects.length})
      </h4>
      {generateKanbanCards()}
      <br />
    </div>
  );
};

/*
 * The Kanban Board Card component
 */
const KanbanCard = ({ onDragEnd, project, setProjects, id }) => {
  const [collapsed, setCollapsed] = useState(true);
  const deleteCard = () =>
    setProjects((projects) => projects.filter((item) => item.id !== id));
  const cardStyle = {
    backgroundColor: "#f9f7f7",
    paddingLeft: "0px",
    paddingTop: "5px",
    paddingBottom: "5px",
    marginLeft: "0px",
    marginRight: "5px",
    marginBottom: "5px",
  };

  return (
    <div
      style={cardStyle}
      draggable={true}
      onDragEnd={(e) => {
        onDragEnd(e, project);
      }}
    >
      {" "}
      <div>Category</div> <button onClick={deleteCard}>delete</button>
      <div>
        <h4>{project.name}</h4>
      </div>
      {collapsed ? null : (
        <div>
          <strong>Description: </strong>
          {project.description}
          <br />
        </div>
      )}
      <div
        style={{ width: "100%" }}
        onClick={(e) => {
          setCollapsed((collapsed) => !collapsed);
        }}
      >
        {collapsed ? String.fromCharCode("9660") : String.fromCharCode("9650")}
      </div>
    </div>
  );
};

/*
 * Projects to be displayed on Kanban Board
 */
let projectList = [
  {
    id: "sada",
    name: "Project 1",
    description:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Etiam posuere dui vel urna egestas rutrum. ",
    project_stage: 1,
  },
  {
    id: "tada",
    name: "Project 2",
    description:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Etiam posuere dui vel urna egestas rutrum. ",
    project_stage: 1,
  },
  {
    id: "pada",
    name: "Project 3",
    description:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Etiam posuere dui vel urna egestas rutrum. ",
    project_stage: 1,
  },
  {
    id: "dada",
    name: "Project 4",
    description:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Etiam posuere dui vel urna egestas rutrum. ",
    project_stage: 2,
  },
];

export default App;
